import React, { useCallback, useRef, useState, useEffect } from 'react';
import { Form, Input, Button } from 'antd';
import { ADD_POST_REQUEST, UPLOAD_IMAGES_REQUEST, REMOVE_IMAGE } from '../reducers/post';
import { useSelector, useDispatch } from 'react-redux';
import { addPost } from '../reducers/post'
// const dummy = {
//   isLoggedIn: true,
//   imagePaths: [],
//   mainPosts: [{
//     User: {
//       id: 1,
//       nickname: '제로초',
//     },
//     content: '첫 번째 게시글',
//     img: 'https://bookthumb-phinf.pstatic.net/cover/137/995/13799585.jpg?udate=20180726',
//   }],
// };



const PostForm = () => {
  const { imagePaths, addPostDone } = useSelector((state) => state.post);
  const [text, setText] = useState('');
  const imageInput = useRef();
  const dispatch = useDispatch();

  const { me } = useSelector((state) => state.user);

  useEffect(() => {
    if (addPostDone) {
      setText('');
    }
  }, [addPostDone]);


  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  const onChangeImages = useCallback((e) => {
    // console.log('images', e.target.files);
    const imageFormData = new FormData();
    [].forEach.call(e.target.files, (f) => {
      imageFormData.append('image', f);
    });
    dispatch({
      type: UPLOAD_IMAGES_REQUEST,
      data: imageFormData,
    });
  }, []);

  const onChangeText = useCallback((e) => {
    setText(e.target.value);
  }, [])

  const onSubmit = useCallback((e) => {

    if (!text || !text.trim()) {
      return alert('게시글을 입력해주세요');
    }

    const formData = new FormData();
    imagePaths.forEach((p) => {
      formData.append('image', p);
    })
    formData.append('content', text);
    return dispatch({
      type: ADD_POST_REQUEST,
      data: formData,
    })
    // dispatch(addPost(text));
  }, [text, imagePaths]);

  const onRemoveImage = useCallback((i) => () => {
    dispatch({
      type: REMOVE_IMAGE,
      data: i
    })
  }, []);


  return (
    <div className="comment_form" style={{ height: '123px', backgroundColor: 'white', padding: '15px 36px 20px 36px' }}>
      <div className="user_comment_form" style={{ display: 'flex', borderBottom: '1px solid black', borderColor: '#e3e6ea', padding: '0 0 10px 0' }}>
        <img style={{ width: '40px', height: '40px', borderRadius: '50%', marginRight: '10px' }} src="https://scontent.ficn3-2.fna.fbcdn.net/v/t1.30497-1/cp0/p50x50/143086968_2856368904622192_1959732218791162458_n.png?_nc_cat=1&ccb=1-5&_nc_sid=7206a8&_nc_ohc=BOw0nnArsGsAX-bGGGQ&_nc_ht=scontent.ficn3-2.fna&oh=1e73ccedbcaebec3c06a8cacdf94601d&oe=6144DDA2" />
        <input placeholder={`${me.nickname} 님, 무슨 일이 있었나요?`} className="comment_input" style={{ width: '100%', borderRadius: '20PX', height: '40px' }} />
      </div>
      <i class="far fa-images fa-2x" style={{ color: 'green' }}></i>
    </div>
  );
};

export default PostForm;


