const initialState = {
    isLoggedIn: false,
    name:'kim',
    age:17
}


export const loginAction = (data) => {
    return{
        type:'LOG_IN',
        data
    }
}

const user = (state=initialState,action) => {
    switch(action.type){
        case 'LOG_IN' : 
            return{
                ...state,
                isLoggedIn:true
            }
        default:
            return{
                ...state,
            }
    }
}
export default user;
