import React, { useCallback,useEffect } from 'react';
import { Button, Form, Input } from 'antd';
import Link from 'next/link';
import {useDispatch, useSelector} from 'react-redux';

import useInput from '../hooks/useInput';
import { loginRequestAction } from '../reducers/user';

const LoginForm = () => {
  const {logInLoading,logInError} = useSelector((state)=>state.user);
  const [email, onChangeEmail] = useInput('');
  const [password, onChangePassword] = useInput('');
  const dispatch = useDispatch();

  useEffect(() => {
    if (logInError) {
      alert(logInError);
    }
  }, [logInError]);

  const onSubmitForm = useCallback(() => {
      dispatch(loginRequestAction({
        email,password
      }));
  }, [email, password]);



  return (
    <Form onFinish={onSubmitForm} style={{ padding: '10px',display:'flex',flexDirection:'column' }}>
      <br />
      <br />

      <div>
        <Input name="user-id" placeholder="이메일 입력해주세요" style={{width:'80%'}} value={email} onChange={onChangeEmail} required />
      </div>
      <div>
        {/* <label htmlFor="user-password">비밀번호</label> */}
        <br />
        <Input name="user-password" placeholder="비밀번호" style={{width:'80%'}} value={password} onChange={onChangePassword} type="password" required />
      </div>
      <div style={{ marginTop: '10px' }}>
        <Button type="primary" htmlType="submit" loading={logInLoading}>로그인</Button>
      </div>
      <br />
      <div>
        <Link href="/signup"><a><Button>회원가입</Button></a></Link>
      </div>
    </Form>
  );
};

export default LoginForm;
