import React, { useEffect, useCallback, useState,useRef } from 'react'
import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { useDispatch, useSelector } from 'react-redux';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import { LOAD_POSTS_REQUEST } from '../reducers/post';
import wrapper from '../store/configureStore';
import { END } from 'redux-saga';
import { Form, Input, Button } from 'antd';
import PostCreatedForm from '../components/PostCreatedForm';
const LandingPage = () => {
    const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);
    const [show,setShow] = useState(false);
    const { imagePaths, addPostDone } = useSelector((state) => state.post);
    const [text, setText] = useState('');
    const imageInput = useRef();
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);
  
    useEffect(() => {
      if (addPostDone) {
        setText('');
      }
    }, [addPostDone]);
  
  
    const onClickImageUpload = useCallback(() => {
      imageInput.current.click();
    }, [imageInput.current]);
  
    const onChangeImages = useCallback((e) => {
      // console.log('images', e.target.files);
      const imageFormData = new FormData();
      [].forEach.call(e.target.files, (f) => {
        imageFormData.append('image', f);
      });
      dispatch({
        type: UPLOAD_IMAGES_REQUEST,
        data: imageFormData,
      });
    }, []);
  
    const onChangeText = useCallback((e) => {
      setText(e.target.value);
    }, [])
  
    const onSubmit = useCallback((e) => {
  
      if (!text || !text.trim()) {
        return alert('게시글을 입력해주세요');
      }
  
      const formData = new FormData();
      imagePaths.forEach((p) => {
        formData.append('image', p);
      })
      formData.append('content', text);
      return dispatch({
        type: ADD_POST_REQUEST,
        data: formData,
      })
      // dispatch(addPost(text));
    }, [text, imagePaths]);
  
    const onRemoveImage = useCallback((i) => () => {
      dispatch({
        type: REMOVE_IMAGE,
        data: i
      })
    }, []);
    useEffect(() => {
        function onScroll() {
            if (window.pageYOffset + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
                if (hasMorePosts && !loadPostsLoading) {
                    const lastId = mainPosts[mainPosts.length - 1]?.id;
                    dispatch({
                        type: LOAD_POSTS_REQUEST,
                        lastId,
                    });
                }
            }
        }
        window.addEventListener('scroll', onScroll);
        return () => {
            window.removeEventListener('scroll', onScroll);
        };
    }, [hasMorePosts, loadPostsLoading, mainPosts]);


    const onShow = () => {
        setShow((prev)=>!prev);
        console.log(show);
        console.log('a');
    }


    return (
        <AppLayout>
            <PostForm onShow={onShow}  />
            {show?
            <>
            </>
            //  <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit} >
            //     <br />
            //     <Input.TextArea value={text} onChange={onChangeText} maxLength={140} placeholder="" style={{borderRadius:'20px',marginBottom:'10px'}} />

            //     <div style={{marginBottom:'10px'}}>

            //     <input type="file" name="image" multiple hidden ref={imageInput} onChange={onChangeImages} />
            //     <Button onClick={onClickImageUpload} style={{position:'relative',left:'10px'}}>이미지 업로드</Button>
            //     <Button type="primary" style={{ float: 'right',position:'relative',right:'10px' }} htmlType="submit">입력</Button>
            //     </div>
            //     <div>
            //     {imagePaths.map((v,i) => {
            //         return (
            //         <div key={v} style={{ display: 'inline-block' }}>
            //             <img src={`http://localhost:3065/${v}`} style={{ width: '200px' }} alt={v} />
            //             <div>
            //             <Button onClick={onRemoveImage(i)}>제거</Button>
            //             </div>
            //         </div>
            //         )
            //     })}
            //     </div>
            // </Form>
            :
            null}
            <div style={{ position: 'relative', top: '30px' }}>
                <PostCreatedForm />
                {mainPosts.map((post) =>
                    <PostCard key={post.id} post={post} />
                )}
            </div>
        </AppLayout>
    )
}

export const getServerSideProps = wrapper.getServerSideProps((store) => async ({ req, res }) => {
    // console.log(req.headers);
    const cookie = req ? req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (req && cookie) {
        axios.defaults.headers.Cookie = cookie;
    }
    store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    store.dispatch({
        type: LOAD_POSTS_REQUEST,
    });
    store.dispatch(END);
    await store.sagaTask.toPromise();
})


export default LandingPage;
