import {all,fork, takeLatest} from 'redux-saga/effects';

function* logIn(action){
    console.log(action);
    try{
        console.log('saga login');
    }catch(err){
        console.error(err);
    }
}

function* watchLogin(){
    console.log('asdasd');
    yield takeLatest('LOG_IN',logIn);
}

export default function* userSaga(){
    yield all([
        fork(watchLogin)
    ])
}
