import produce from "immer"

const initialState = {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
}


export const loginAction = (data) => {
    return{
        type:'LOG_IN',
        data
    }
}

export const logoutAction = () => {
    return{
        type:'LOG_OUT'
    }
}

const user = (state=initialState,action)=> produce(state,(draft)=>{
    switch(action.type){
        case 'LOG_IN' : 
                draft.isLoggedIn=true
        // case 'LOG_OUT' :
        //     return{
        //         ...state,
        //         isLoggedIn:false
        //     }
        
        default:
            return{
                ...state,
            }
    }
})

export default user;
