import {all,call,delay,fork, put, take, takeLatest} from 'redux-saga/effects';
import { LOG_IN_FAILURE, LOG_IN_REQUEST,LOG_OUT_SUCCESS,LOG_OUT_FAILURE,LOG_OUT_REQUEST,LOG_IN_SUCCESS, SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, CHANGE_NICKNAME_REQUEST } from '../reducers/user';
import axios from 'axios';


function logInApi(data){
    return axios.post('http://localhost:3065/user/login',data);
}

function* logIn(action){
    try{
        const result = yield call(logInApi,action.data)
        yield put({
            type:LOG_IN_SUCCESS,
            data:result.data
        })
    }catch(err){
        console.error(err);
        yield put({
            type:LOG_IN_FAILURE,
            // error:err.response.data,
        })
    }
}

function logOutApi() {
    return axios.post('http://localhost:3065/user/logout');
  }
  

function* logOut(){
    try {
        yield call(logOutApi);
        console.log('여기까진 실행되나?')
        yield put({
          type: LOG_OUT_SUCCESS,
        });
      } catch (err) {
        console.error(err);
        yield put({
          type: LOG_OUT_FAILURE,
          error: err.response.data,
        });
      }
}

function signUpApi(data){
    return axios.post('http://localhost:3065/user',data);
}

function* signUp(action){
    try{
       const result = yield call(signUpApi,action.data);
       console.log('asdasd123123');
       console.log(result);
       yield put({
            type:SIGN_UP_SUCCESS,
       })
    }catch(err){
        console.error(err);
        yield put({
            type:SIGN_UP_FAILURE,
            error:err.response.data,
        })
    }
}

function changeNicknameAPI(data){
    return axios.patch('/user/nickname',{nickname:data});
}

function* changeNickname(action) {
    try {
      const result = yield call(changeNicknameAPI, action.data);
      yield put({
        type: CHANGE_NICKNAME_SUCCESS,
        data: result.data,
      });
    } catch (err) {
      console.error(err);
      yield put({
        type: CHANGE_NICKNAME_FAILURE,
        error: err.response.data,
      });
    }
  }

function* watchSignUp(){
    yield takeLatest(SIGN_UP_REQUEST,signUp);
}

function* watchLogin(){
    yield takeLatest(LOG_IN_REQUEST,logIn);
}

function* watchLogOut(){
    yield takeLatest(LOG_OUT_REQUEST,logOut);
}
function* watchChangeNickname(){
    yield takeLatest(CHANGE_NICKNAME_REQUEST,changeNickname)
}

export default function* userSaga(){
    yield all([
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchSignUp),
        fork(watchChangeNickname),
    ])
}
