import React, { useCallback, useRef,useState } from 'react';
import { Form, Input, Button } from 'antd';
import { ADD_POST_REQUEST,UPLOAD_IMAGES_REQUEST } from '../reducers/post';
import { useSelector,useDispatch } from 'react-redux';
import {addPost} from '../reducers/post'
const dummy = {
  isLoggedIn: true,
  imagePaths: [],
  mainPosts: [{
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 번째 게시글',
    img: 'https://bookthumb-phinf.pstatic.net/cover/137/995/13799585.jpg?udate=20180726',
  }],
};

const PostForm = () => {
  const { imagePaths, addPostDone } = useSelector((state) => state.post);
  const [text,setText] = useState('');
  const imageInput = useRef();
  const dispatch = useDispatch();

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  // const onChangeImages = useCallback((e) => {
  //   console.log('images', e.target.files);
  //   const imageFormData = new FormData();
  //   [].forEach.call(e.target.files, (f) => {
  //     imageFormData.append('image', f);
  //   });
  //   dispatch({
  //     type: UPLOAD_IMAGES_REQUEST,
  //     data: imageFormData,
  //   });
  // }, []);

  const onChangeText = useCallback((e)=> {
     setText(e.target.value);
  },[])

  const onSubmit = useCallback((e)=> {
      dispatch(addPost(text));
  },[text])


  return (
    <Form style={{ margin: '10px 0 20px' }} encType="multipart/form-data" onFinish={onSubmit} >
      <Input.TextArea value={text} onChange={onChangeText} maxLength={140} placeholder="" />
      <div>
        <input type="file" name="image" multiple hidden ref={imageInput} onChange={onChangeImage} />
        <Button onClick={onClickImageUpload}>이미지 업로드</Button>
        <Button type="primary" style={{ float: 'right' }} htmlType="submit">입력</Button>
      </div>
      <div>
        {dummy.imagePaths.map((v) => {
          return (
            <div key={v} style={{ display: 'inline-block' }}>
              <img src={`http://localhost:3065/${v}`} style={{ width: '200px' }} alt={v} />
              <div>
                <Button>제거</Button>
              </div>
            </div>
          )
        })}
      </div>
    </Form>
  );
};

export default PostForm;
