import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Col, Input, Menu, Row } from 'antd';
import { useSelector } from 'react-redux';
import LoginForm from './LoginForm';
import UserProfile from './UserProfile';
// import { useLayoutEffect } from 'react';

const dummy = {
  nickname: '제로초',
  Post: [],
  Followings: [],
  Followers: [],
  isLoggedIn: false,
};

const AppLayout = ({ children }) => {
  const {me} = useSelector(state=>state.user);
  // const {mainPosts} = useSelector((state)=>state.post);

  return (
    <div style={{backgroundColor:"#eff2f5"}}>
      <Menu mode="horizontal" style={{display:'flex',position:'fixed',zIndex:'300'}} >
        <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
        <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
        <Menu.Item key="mail">
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
        </Menu.Item>
      </Menu>
      <Row gutter={8} style={{position:'relative',top:'60px'}}>
        {/* <Col xs={24} md={8}>
          {me
            ? <UserProfile />
            
            : <LoginForm />}
        </Col> */}
        <Col xs={24} md={4}>
          {/* 여기다가 링크를 우선 만들어두고 */}
          <div className="userProfile">
            <img style={{borderRadius:'34px'}} src="https://scontent.ficn3-2.fna.fbcdn.net/v/t1.30497-1/cp0/p40x40/143086968_2856368904622192_1959732218791162458_n.png?_nc_cat=1&ccb=1-5&_nc_sid=dbb9e7&_nc_ohc=1rdHoPUFiSAAX9dHe3q&tn=BKIHErudAlna7iLo&_nc_ht=scontent.ficn3-2.fna&oh=d0831d5eb00febe5c25774f8980d4636&oe=614557A3"/>

          </div>

          <div className="friend">
            <img src="https://static.xx.fbcdn.net/rsrc.php/v3/y8/r/S0U5ECzYUSu.png"/>
            <span>친구</span>
          </div>
        </Col>
        <Col xs={24} md={16}>
          {children}
        </Col>
        <Col xs={24} md={4}>
          {/* 여기다가는 뭐다른걸 넣어놓고 */}
        </Col>
      </Row>
    </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
}

export default AppLayout;
