const initialState = {
    name:'kim',
    age:17
}


export const loginAction = (data) => {
    return{
        type:LOG_IN,
        data
    }
}

export const user = (state=initialState,action) => {
    switch(action.type){
        case LOG_IN : 
            return{
                ...state,
                name:action.data
            }
        default:
            return{
                ...state,
            }
    }
}
