import { all, takeLatest } from "redux-saga/effects";
import { ADD_POST_REQUEST } from "../reducers/post";



function* watchAddPost(){
    yield takeLatest(ADD_POST_REQUEST,addPost);
}


export default function* postSaga(){
    yield all([
        fork(watchAddPost)
    ])
}
