const initialState = {
    isLoggedIn: false,
    name:'kim',
    age:17
}


export const loginAction = (data) => {
    return{
        type:'LOG_IN',
        data
    }
}

export const logoutAction = () => {
    return{
        type:'LOG_OUT'
    }
}

const user = (state=initialState,action) => {
    switch(action.type){
        case 'LOG_IN' : 
            return{
                ...state,
                isLoggedIn:true
            }
        case 'LOG_OUT' :
            return{
                ...state,
                isLoggedIn:false
            }
        
        default:
            return{
                ...state,
            }
    }
}
export default user;
